mapper.insert:
INSERT INTO outbox_event (oeid, event_type, aggregate_id, payload, status, created_at) VALUES
(#{event.oeid}, #{event.eventType}, #{event.aggregateId}, #{event.payload}, #{event.status}, #{event.createdAt})
ON CONFLICT (oeid, created_at) DO UPDATE SET event_type = EXCLUDED.event_type, aggregate_id = EXCLUDED.aggregate_id, payload = EXCLUDED.payload, status = EXCLUDED.status;
