changeSet.sql:
CREATE USER IF NOT EXISTS "payment-consumer" WITH PASSWORD 'payment-consumer';
GRANT CONNECT ON DATABASE payment TO "payment-consumer";
GRANT USAGE ON SCHEMA public TO "payment-consumer";
GRANT SELECT, INSERT, UPDATE, DELETE ON ALL TABLES IN SCHEMA public TO "payment-consumer";
ALTER DEFAULT PRIVILEGES IN SCHEMA public GRANT SELECT, INSERT, UPDATE, DELETE ON TABLES TO
"payment-consumer";
