targets.expr:
sum by (pod) (
  rate(http_server_requests_seconds_count{
    application="payment-service",
    uri="/payments",
    method="POST"
  }[$__rate_interval])
)

100 * (
  hikaricp_connections_active{
    application="payment-service", pool="web-pool"
  }
/
  hikaricp_connections_max{
    application="payment-service", pool="web-pool"
  }
)

max by (pod, pool) (
  hikaricp_connections_pending{
    application="payment-service",
    namespace="payment",
    pool="web-pool"
  }
)

100 * (
  hikaricp_connections_active{
    application="payment-service", pool="outbox-pool"
  }
/
  hikaricp_connections_max{
    application="payment-service", pool="outbox-pool"
  }
)

max by (pod, pool) (
  hikaricp_connections_pending{
    application="payment-service",
    namespace="payment",
    pool="outbox-pool"
  }
)

100 *
sum by (application, pod) (
  jvm_memory_used_bytes{application=~"payment-service", area="heap"}
)
/
sum by (application, pod) (
  jvm_memory_max_bytes{application=~"payment-service", area="heap"}
)

(
  100 *
  sum by (application, pod) (
    jvm_memory_used_bytes{application=~"payment-service", area="heap"}
  )
  /
  sum by (application, pod) (
    jvm_memory_max_bytes{application=~"payment-service", area="heap"}
  )
)
and on (application, pod)
(
  100 *
  sum by (application, pod) (
    jvm_memory_used_bytes{application=~"payment-service", area="heap"}
  )
  /
  sum by (application, pod) (
    jvm_memory_max_bytes{application=~"payment-service", area="heap"}
  ) > 60
)

100 * (
  hikaricp_connections_active{
    application="payment-consumers", pool="HikariPool-1"
  }
/
  hikaricp_connections_max{
    application="payment-consumers", pool="HikariPool-1"
  }
)

max by (pod, pool) (
  hikaricp_connections_pending{
    application="payment-consumers",
    namespace="payment",
    pool="HikariPool-1"
  }
)

100 *
sum by (application, pod) (
  jvm_memory_used_bytes{application=~"payment-consumers", area="heap"}
)
/
sum by (application, pod) (
  jvm_memory_max_bytes{application=~"payment-consumers", area="heap"}
)

(
  100 *
  sum by (application, pod) (
    jvm_memory_used_bytes{application=~"payment-consumers", area="heap"}
  )
  /
  sum by (application, pod) (
    jvm_memory_max_bytes{application=~"payment-consumers", area="heap"}
  )
)
and on (application, pod)
(
  100 *
  sum by (application, pod) (
    jvm_memory_used_bytes{application=~"payment-consumers", area="heap"}
  )
  /
  sum by (application, pod) (
    jvm_memory_max_bytes{application=~"payment-consumers", area="heap"}
  ) > 60
)

sum by (topic) (
  max by (topic, partition) (
    kafka_topic_partition_current_offset{
      topic=~"^payment.*\\.DLQ$"
    }
  )
)
