targets.expr:
sum by (pod) (
  rate(http_server_requests_seconds_count{
    application="payment-service",
    uri="/payments",
    method="POST"
  }[$__rate_interval])
)

100 *
sum by (application, pod) (
  jvm_memory_used_bytes{application=~"payment-(service|consumers)", area="heap"}
)
/
sum by (application, pod) (
  jvm_memory_max_bytes{application=~"payment-(service|consumers)", area="heap"}
)

(
  100 *
  sum by (application, pod) (
    jvm_memory_used_bytes{application=~"payment-(service|consumers)", area="heap"}
  )
  /
  sum by (application, pod) (
    jvm_memory_max_bytes{application=~"payment-(service|consumers)", area="heap"}
  )
)
and on (application, pod)
(
  100 *
  sum by (application, pod) (
    jvm_memory_used_bytes{application=~"payment-(service|consumers)", area="heap"}
  )
  /
  sum by (application, pod) (
    jvm_memory_max_bytes{application=~"payment-(service|consumers)", area="heap"}
  ) > 60
)

sum by (topic) (
  max by (topic, partition) (
    kafka_topic_partition_current_offset{
      topic=~"^payment.*\\.DLQ$"
    }
  )
)

sum by (consumergroup, topic) (
  max by (consumergroup, topic, partition) (kafka_consumergroup_current_offset)
)

sum by (topic) (
  max by (topic, partition) (
    kafka_topic_partition_current_offset{
      topic=~"^payment.*",
      topic!~".*\\.DLQ$"
    }
  )
)
